<script type="text/javascript">
jQuery.noConflict();
jQuery(document).ready(function($) {

$('.homemenu').addClass('active');
setTimeout( function(){
   $('.animateRun').viewportChecker();
	},1000);

    var owl = $('#defaultBanner');
