        margin: 0,
        mouseDrag: false,
        nav: false,
        navSpeed: 50,
        items: 1,
        loop: false,
        dots: true,
        lazyLoad: true,
        autoplay: true,
        animateOut: 'fadeOut',
        animateIn: 'fadeIn',
        autoplayHoverPause: true
    });

    function setAnimation(_elem, _InOut) {
        var animationEndEvent = 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend';
        _elem.each(function() {
            var $elem = $(this);
            var $animationType = 'animated ' + $elem.data('animation-' + _InOut);
            $elem.addClass($animationType).one(animationEndEvent, function() {
                $elem.removeClass($animationType);
            });
        });
    }

    owl.on('change.owl.carousel', function(event) {
        var $currentItem = $('.owl-item', owl).eq(event.item.index);
        var $elemsToanim = $currentItem.find("[data-animation-out]");
        setAnimation($elemsToanim, 'out');
    });

    var round = 0;
    owl.on('changed.owl.carousel', function(event) {
        var $currentItem = $('.owl-item', owl).eq(event.item.index);
        var $elemsToanim = $currentItem.find("[data-animation-in]");

        setAnimation($elemsToanim, 'in');
    })

    owl.on('translated.owl.carousel', function(event) {
        //console.log(event.item.index, event.page.count);
        if (event.item.index == (event.page.count - 1)) {
            if (round < 1) {
                round++
                console.log(round);
            } else {
                owl.trigger('stop.owl.autoplay');
                var owlData = owl.data('owl.carousel');
                owlData.settings.autoplay = false;
                owlData.options.autoplay = false;
                owl.trigger('refresh.owl.carousel');
            }
        }
    });

    $(document).on('keydown', '.trimSpace', function(e) {
        if (e.which === 32 && e.target.selectionStart === 0) {
            return false;
        }
    });

			// HEADER MENU FUNCTION 
			if ($(window).width() <= 768) {
								$(document).on('click', '.mobileMenu', function(e) {
											e.preventDefault();
											var data = $(this).next('.rightShow').slideToggle()
							});
			} else {
							$('.linkMenu').hover(function(e) {
											e.preventDefault();
											var data = $(this).next('.rightShow').html();
											$('.rightMenu').html(data);
							});
			}
			
			
			$(document).on('click', function(e) {
							var $trigger = $('.menuToggler');
							if (!$trigger.is(e.target) && $trigger.has(e.target).length === 0) {
											$('header nav').slideUp();
											$('.menuToggler').removeClass('active');
							}
			});

		$(document).on('click', 'header nav, .mobileMenu', function(e) {
    e.stopPropagation();
				});

});
</script>

@endsection
